mapper.select:
SELECT id, name, description, modifyTime, lastModifyTime, beginTime, endTime, templateUrl, status
FROM tb_questionnaire_template_edit
WHERE id = #{questionnaireTemplateId, jdbcType=INTEGER}

mapper.update:
UPDATE tb_questionnaire_template_edit
description = #{description, jdbcType=VARCHAR},
name = #{name, jdbcType=VARCHAR}, modifyTime = #{modifyTime, jdbcType=TIMESTAMP}, lastModifyTime = #{lastModifyTime}, beginTime = #{beginTime, jdbcType=TIMESTAMP}, endTime = #{endTime, jdbcType=TIMESTAMP}, templateUrl = #{templateUrl, jdbcType=VARCHAR}, status = #{status, jdbcType=INTEGER}
WHERE id = #{id, jdbcType=INTEGER}

mapper.select:
SELECT id, name, description, modifyTime, lastModifyTime, beginTime, endTime, templateUrl, status
FROM tb_questionnaire_template_edit
LIMIT #{offset, jdbcType=INTEGER}, #{pageSize, jdbcType=INTEGER}
