mapper.update:
UPDATE tb_questionnaire_content
jsonContent = #{jsonContent, jdbcType=VARCHAR},
lastModifyTime = #{lastModifyTime, jdbcType=TIMESTAMP},
productionLine = #{productionLine}, modifyTime = #{modifyTime}, lastModifyTime = #{lastModifyTime}, status = #{status}
WHERE id = #{id} AND modifyTime = #{lastModifyTime}
