mapper.select:
SELECT id, name, description, modifyTime, lastModifyTime, beginTime, endTime, templateJspUrl, status
FROM tb_questionnaire_template_edit
WHERE questionnaireTemplateId = #{questionnaireTemplateId}

mapper.update:
UPDATE tb_questionnaire_template_edit
description = #{description},
name = #{name}, modifyTime = #{modifyTime}, lastModifyTime = #{lastModifyTime}, beginTime = #{beginTime}, endTime = #{endTime}, templateJspUrl = #{templateJspUrl}, status = #{status}
WHERE id = #{id}

mapper.select:
SELECT id, name, description, modifyTime, lastModifyTime, beginTime, endTime, templateJspUrl, status
FROM tb_questionnaire_template_edit
LIMIT #{offset}, #{pageSize}
